import { Component, OnInit } from '@angular/core';
import { Sesamien } from '../sesamien';
import { Router } from '@angular/router';
import { SesamienService } from '../sesamien.service';

@Component({
  selector: 'app-list-sesamien',
  templateUrl: './list-sesamien.component.html'
})
export class ListSesamienComponent implements OnInit {
  sesamienList: Sesamien[];

  constructor(
    private router: Router,
    private sesamienService: SesamienService
  ) {}

  ngOnInit(){
    this.sesamienService.getSesamienList()
      .subscribe(sesamienList => this.sesamienList = sesamienList); 
  }

  goToSesamien(sesamien: Sesamien){
    this.router.navigate(['/sesamien',sesamien.id]);
  }

}
<div class="container">
  <div class="row">
    <app-search-sesamien></app-search-sesamien>

    <div *ngFor="let sesamien of sesamienList" class="col m4 s6">
      <div
        (click)="goToSesamien(sesamien)"
        class="card horizontal"
        appBorderCard
      >
        <div class="card-image">
          <img [src]="" />
        </div>
        <div class="card-stacked">
          <div class="card-content">
            <p>{{ sesamien.name }}</p>
            <p>
              <small>{{ sesamien.created | date : "dd/MM/yy" }}</small>
            </p>
            <span
              *ngFor="let mention of sesamien.mentions"
              class="{{ mention | sesamienMentionColor }}"
            >
              {{ mention }}
            </span>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
<a
  class="btn-floating btn-large waves-effect waves-light red z-depth-3"
  style="position: fixed; bottom: 25px; right: 25px"
  routerLink="/sesamien/add"
>
  +
</a>
